import React, { useState } from "react";
import iconPath from "./icons.svg";
import "./Button.css";

import APISample from "../../API_SAMPLE.json";

/**
 * <Button
 *   className="MyButton"
 *   onClick={() => console.log('Click')} 
 * />
 *
 * @prop {Function} onClick
 * @prop {mixed} ... All other props will be forwarded to the native DOM button.

 */
function Button(props) {
  const { onClick, className, ...otherProps } = props;

  const [filteredData, setFilteredData] = useState([]);
  const [wordEntered, setWordEntered] = useState("");
  const [err, setErr] = useState('');

  const handleClick = async () => {
    try {
      const response = await fetch(APISample, {
        method: 'GET',
        headers: {
          Accept: 'application/json',
        },
      });

      if (!response.ok) {
        throw new Error(`Error! status: ${response.status}`);
      }

      const result = await response.json();

      console.log('result is: ', JSON.stringify(result, null, 4));

      setFilteredData(result);
    } catch (err) {
      setErr(err.message);
    }
  };

  console.log(filteredData);

  return (
    <div>
    <button
      type="button"
      className={"Button" + (className || "")}
      // onClick={onClick}
      onClick={handleClick}
      {...otherProps}
    >
      <svg viewBox="0 0 24 24" width="24" height="16">
        <use xlinkHref={iconPath + "#dls-icon-arrow-right"} />
      </svg>
    </button>
    {/* {filteredData.filteredData.map(person => {
        return (
          <div key={person.id}>
            <h2>{person.email}</h2>
            <h2>{person.first_name}</h2>
            <h2>{person.last_name}</h2>
            <br />
          </div>
        );
    })} */}
    </div>
  );
}

export default Button;
