import React, { useState } from "react";
import iconPath from "./icons.svg";
import "./Button.css";

import APISample from "../../API_SAMPLE.json";

/**
 * <Button
 *   className="MyButton"
 *   onClick={() => console.log('Click')} 
 * />
 *
 * @prop {Function} onClick
 * @prop {mixed} ... All other props will be forwarded to the native DOM button.

 */
function Button(props) {
  const { onClick, className, ...otherProps } = props;

  const [data, setData] = useState({data: []});
  const [isLoading, setIsLoading] = useState(false);
  const [err, setErr] = useState('');

  const handleClick = async () => {
    setIsLoading(true);

    try {
      const response = await fetch('https://reqres.in/api/users', {
        method: 'GET',
        headers: {
          Accept: 'application/json',
        },
      });

      if (!response.ok) {
        throw new Error(`Error! status: ${response.status}`);
      }

      const result = await response.json();

      console.log('result is: ', JSON.stringify(result, null, 4));

      setData(result);
    } catch (err) {
      setErr(err.message);
    } finally {
      setIsLoading(false);
    }
  };

  console.log(data);



  return (
    <div>
      <button
        type="button"
        className={"Button" + (className || "")}
        // onClick={onClick}
        onClick={handleClick}
        {...otherProps}
      >
        <svg viewBox="0 0 24 24" width="24" height="16">
          <use xlinkHref={iconPath + "#dls-icon-arrow-right"} />
        </svg>
      </button>

    </div>
  );
}

export default Button;
