// Import React
import React, { useState } from "react";

// Import Component
import Button from "../Button/Button"
import "./Input.css";
import "../ResultsList/ResultsList.css"

// Import API Data
import API_SAMPLE from "../../API_SAMPLE.json"

export default function Input() {
  const [value, setValue] = useState("");

  const onChange = (event) => {
    setValue(event.target.value);
  };

  const onSearch = (searchTerm) => {
    setValue(searchTerm);
    // our api to fetch the search result
    console.log("search ", searchTerm);
  };

  return (
    <div className="App">
      <h1>Search</h1>

      <div className="search-container">
        <div className="search-inner">
          <input type="text" value={value} onChange={onChange} />
          <button onClick={() => onSearch(value)}> Search </button>
        </div>
        <div className="dropdown">
          {API_SAMPLE
            .filter((item) => {
              const searchTerm = value.toLowerCase();
              const suburb = item.name.toLowerCase() || item.state.abbreviation.toLowerCase();

              return (
                searchTerm &&
                suburb.startsWith(searchTerm) &&
                suburb !== searchTerm
              );
            })
            .slice(0, 10)
            .map((item) => (
              <div
                onClick={() => onSearch(item.name || item.state.abbreviation.toLowerCase())}
                className="dropdown-row"
                key={item.name || item.state.abbreviation}
              >
                {item.name + ', ' + item.state.abbreviation}
              </div>
            ))}
        </div>
      </div>
    </div>
  );
}


