import React, { useState, useEffect } from "react";
import TextField from "@mui/material/TextField";
import Autocomplete from '@mui/material/Autocomplete';
import { Box } from "@mui/system";
import "./Input.css";
import { ResultsList } from "../ResultsList/ResultsList"

/**
 * <Input
 *   className="MyInput"
 *   data-something="Value"
 *   value="Hello, World!"
 *   onChange={(value) => console.log('You typed', value)}
 * />
 *
 * @prop {string} value The default value for the input.
 * @prop {string} placeholder The placeholder text.
 * @prop {Function} onChange Callback that will receive current input value.
 * @prop {mixed} ... All other props will be forwarded to the native DOM input.
 */
export function Input(props) {
  const { className, value, onChange, ...otherProps } = props;

  const API_SAMPLE = [
    { name: "Sydney South", state: { abbreviation: "NSW" } },
    { name: "Sydney", state: { abbreviation: "NSW" } },
    { name: "Sydney International Airport", state: { abbreviation: "NSW" } },
    { name: "Sydney Domestic Airport", state: { abbreviation: "NSW" } },
    { name: "Sydenham", state: { abbreviation: "VIC" } }
  ];

  // const [inputValue, setInputValue] = useState(value);

  const [inputValue, setInputValue] = useState([]);

  useEffect(() => {
    fetch('https://www.balldontlie.io/api/v1/players')
    .then((response) => response.json())
    .then((json) => setInputValue(json.data))
  }, []);
  console.log(inputValue)

  // Keep the current value, unless the parent component supplies a different "value" prop.
  // useEffect(() => {
  //   setInputValue(value);
  // }, [value]);

  function handleChange(event) {
    setInputValue(event.target.value);
    onChange && onChange(event.target.value);
  }

  return (
    // <input
    //   className={"Input " + (className || "")}
    //   type="text"
    //   value={inputValue}
    //   onChange={handleChange}
    //   {...otherProps}
    // />
    <Autocomplete 
      id="abc"
      // getOptionLable={(inputValue) => `${inputValue.name} ${inputValue.state.abbreviation}`}
      options={inputValue}
      isOptionEqualToValue={(option, value) =>
          option.name === value.name
      }
      noOptionsText={"No Available Data"}
      renderOption={(props, inputValue) => (
        <Box component="li" {...props} key={inputValue.id}>
          {inputValue.name} {inputValue.state.abbreviation}
        </Box>
      )}
      renderInput={(params) => <TextField {...params} label="Search for a suburb" />}
    />
  );
}
